mapper.update:
update app_table
SET modifytime = (select now()), shortdesc=#{shortDesc}, provider =#{provider}, appname=
#{appName}, applicationType=#{applicationType}, appIntroduction=#{appIntroduction},
downloadcount=#{downloadCount},AFFINITY=#{affinity},INDUSTRY=#{industry},contact=#{contact},USERID=#{userId}
,SCORE=(SELECT AVG(score) FROM public.csar_package_score WHERE APPID = #{appId})
WHERE APPID = #{appId}

mapper.select:
select count(*) from app_table


        and appname = #{appName}


        and provider = #{provider}


        and applicationtype = #{applicationType}


        and affinity = #{affinity}


        and userid = #{userId}


limit #{limit} offset #{offset}

select * from app_table


        and appid = #{appId}


        and appname = #{appName}


        and provider = #{provider}


        and applicationtype = #{applicationType}


        and affinity = #{affinity}


        and userid = #{userId}


limit #{limit} offset #{offset}
