mapper.update:
update app_store_table
SET appstorename = #{appStoreName}, appstoreversion = #{appStoreVersion}, company = #{company},
url = #{url}, schema = #{schema}, apppushintf = #{appPushIntf},
appdtransid = #{appdTransId}, modifiedtime = (select now()), description = #{description}
WHERE appstoreid = #{appStoreId}

mapper.select:
select appstoreid, appstorename, appstoreversion, company, url, schema, apppushintf,
apptransid, addedtime, modifiedtime, description from app_store_table


        and appstorename like #{appStoreName}


        and company like #{company}
